mapper.select:
    select ms.messageNo,ms.sender,ms.receiver, ms.messageContent, ms.messageDate, m2.profileIMG 
from message ms, member m2 
where  ms.sender=m2.id  and  ms.messageState=1 and  ms.receiver=#{id}

select ms.messageNo, ms.sender, ms.messageContent, ms.messageDate , m2.profileIMG 
from message ms, member m2 
where  ms.sender=m2.id and messageNo=#{value}
